    @staticmethod
    def load_config():
        with open('config.json') as f:
            return json.load(f)

    def check_liq(self) -> bool:
        pair = self.pancake_factory_contract.functions.getPair(self.bnb_address,
                                                               self.web3.toChecksumAddress(
                                                                   self.config['common']['target_address'])).call()
        try:
            pair.index("0x0000000")
            raise Exception("not start")
        except Exception as e:
            return True

    def buy(self):
        txn = self.pancake_router_contract.functions.swapExactETHForTokens(
            0,
            [self.bnb_address, self.config['common']['target_address']],
            self.config['common']['from_address'],
            (int(time.time()) + 1000000)
        ).buildTransaction({
            'from': self.config['common']['from_address'],
            'value': self.web3.toWei(self.config['buy']['amount'], 'ether'),
            # 'gas': self.config['buy']['gas'],
            # 'gasPrice': self.web3.toWei(self.config['buy']['gas_price'], self.config['buy']['unit']),
            'nonce': self.web3.eth.get_transaction_count(self.config['common']['from_address']),
        })
        signed_txn = self.web3.eth.account.sign_transaction(txn,
                                                            private_key=self.config['common']['private_key'])
        tx_hash = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
        try:
            self.web3.eth.wait_for_transaction_receipt(tx_hash, timeout=20)
            print("买入完成，查看结果：https://bscscan.com/tx/{}".format(self.web3.toHex(tx_hash)))
        except Exception as e:
            raise e

    def get_balance(self):
        return self.target_contract.functions.balanceOf(
            self.web3.toChecksumAddress(self.config['common']['from_address'])).call()

    def approve(self):
        tx = self.target_contract.functions.approve(self.pancake_router_address, self.get_balance()).buildTransaction({
            'from': self.config['common']['from_address'],
            'gasPrice': self.web3.toWei('5', 'gwei'),
            'nonce': self.web3.eth.get_transaction_count(self.config['common']['from_address']),
        })
        signed_txn = self.web3.eth.account.sign_transaction(tx, private_key=self.config['common']['private_key'])
        tx_hash = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
        try:
            self.web3.eth.wait_for_transaction_receipt(tx_hash, timeout=20)
            print("approve完成，查看结果：https://bscscan.com/tx/{}".format(self.web3.toHex(tx_hash)))
        except Exception as e:
            raise e

    def sell(self, amount):
        txn = self.pancake_router_contract.functions.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount,
            0,
            [self.config['common']['target_address'], self.bnb_address],
            self.config['common']['from_address'],
            (int(time.time()) + 1000000)
        ).buildTransaction({
            'from': self.config['common']['from_address'],
            # 'gas': self.config['sell']['gas'],
            # 'gasPrice': self.web3.toWei(self.config['sell']['gas_price'], self.config['sell']['unit']),
            'nonce': self.web3.eth.get_transaction_count(self.config['common']['from_address']),
        })
        signed_txn = self.web3.eth.account.sign_transaction(txn, private_key=self.config['common']['private_key'])
        tx_hash = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
        try:
            self.web3.eth.wait_for_transaction_receipt(tx_hash, timeout=20)
            print("卖出完成，查看结果：https://bscscan.com/tx/{}".format(self.web3.toHex(tx_hash)))
        except Exception as e:
            raise e


